import React from 'react';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      '& .MuiTextField-root': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }),
);

const ThreadForm: React.FC =  () => {
  const classes = useStyles();

  const [texts, setTexts] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setTexts(e.target.value)
  }

  return (
    <React.Fragment>
      <div>
        <div>レスを投稿する</div>
        <form>
            <input style={{display: 'block'}} placeholder={'名前(省略可)'} size={70}/>
            <input style={{display: 'block'}} placeholder={'メールアドレス(省略可)'} size={70}/>
            <textarea style={{display: 'block'}} placeholder={"コメント内容"} rows={5} cols={70}/>
            <input style={{display: 'block'}} type={"submit"} value={"書き込む"}/>
        </form>
      </div>
    </React.Fragment>
  );
}

export default ThreadForm;
