import React, {useState} from 'react';
import Container from '@material-ui/core/Container';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import axios from "axios";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary,
    },
  }),
);

type PostType = {
  id: number;
  name: string;
  message: string;
  ip: string;
  week: string;
}

const Bbs: React.FC =  () => {
  const [posts, setPosts] = useState<Array<PostType>>([]);
  const [texts, setTexts] = useState('');
  const classes = useStyles();


  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setTexts(e.target.value)
  }

  axios.get('http://127.0.0.1:8000/bbs/index/').then((res) => {
    setPosts(res.data)
  })

  return (
    <React.Fragment>
      <main>
        <Container>
          <div className={classes.root}>
            <Grid container spacing={3}>
              {posts.map((post) => (
                <div key={post.id}>
                  <Grid item xs={9}>
                  <div className="meta">
                <span className="number">{post.id}</span>
                <span className="name">
                  <b><a href="mailto:sage">{post.name}</a></b>
                </span>
                <span className="date">2021/05/24(月) 12:55:00.32</span>
                <span className="uid">{post.ip}</span>
              </div>
              <div className="message">
                <span className="escaped">{post.message}</span>
              </div>
                  </Grid>
                  <Grid item xs={3}>
                    bbbbbb
                  </Grid>
                </div>
              ))}
            </Grid>
          </div>

          <div className={classes.root}>
            <Grid container spacing={3}>
              <Grid item xs={9}>
                <Paper className={classes.paper}>xs=9</Paper>
              </Grid>
              <Grid item xs={3}>
                <Paper className={classes.paper}>aside</Paper>
              </Grid>
            </Grid>
          </div>

          {posts.map((post) => (
            <div key={post.id}>
              <div className="meta">
                <span className="number">{post.id}</span>
                <span className="name">
                  <b><a href="mailto:sage">{post.name}</a></b>
                </span>
                <span className="date">2021/05/24(月) 12:55:00.32</span>
                <span className="uid">{post.ip}</span>
              </div>
              <div className="message">
                <span className="escaped">{post.message}</span>
              </div>
            </div>
          ))}
          <form noValidate autoComplete="off">
            <textarea value={texts} onChange={handleChange} />
          </form>

        </Container>
      </main>
    </React.Fragment>
  );
}

export default Bbs;
