import React, {useState} from 'react';
import styled from 'styled-components'

const DivStyled = styled.div`
  font-size: 20px;
  margin-top: 90px;
`

const ThreadForm: React.FC =  () => {
  const [texts, setTexts] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setTexts(e.target.value)
  }
  console.log(texts)
  return (
    <React.Fragment>
      <div style={{fontSize: 20, marginTop: 930}}>
        <div style={{fontSize: 20, marginTop: 20, borderBottom: '1px dotted #dcdcdc', paddingBottom: '5', marginBottom: '1em'}}>レスを投稿する</div>
        <form>
          <input style={{display: 'block'}} placeholder={'名前(省略可)'} size={70}/>
          <input style={{display: 'block'}} placeholder={'メールアドレス(省略可)'} size={70}/>
          <textarea style={{display: 'block'}} placeholder={"コメント内容"} rows={5} cols={70} value={texts} onChange={handleChange}/>
          <input style={{display: 'block'}} type={"submit"} value={"書き込む"}/>
        </form>
      </div>
    </React.Fragment>
  );
}

export default ThreadForm;
