import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import styled from 'styled-components';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom';
import CommentIcon from '@material-ui/icons/Comment';
import ForumIcon from '@material-ui/icons/Forum';


const Main = styled.main`
  background-color: #f2f3f7;
  padding: 70px 0px 50px;
`

const Home: React.FC =  () => {

  const useStyles = makeStyles((theme) => ({
    icon: {
      marginRight: theme.spacing(2),
    },
    heroContent: {
      padding: theme.spacing(8, 0, 6),
    },
    heroButtons: {
      marginTop: theme.spacing(4),
    },
    cardGrid: {
      paddingTop: theme.spacing(8),
      paddingBottom: theme.spacing(8),
    },
    card: {
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
    },
    cardMedia: {
      paddingTop: '56.25%', // 16:9
    },
    cardContent: {
      flexGrow: 1,
    },
    footer: {
      backgroundColor: theme.palette.background.paper,
      padding: theme.spacing(6),
    },
  }));

  const classes = useStyles();
  const cards= [{title: '掲示板', description: '気ままに', icon: <CommentIcon/>, url: '/thread'},
                {title: 'チャット', description: '気ままに', icon: <ForumIcon/>, url: '/chat'}];


  return (
    <React.Fragment>
      <Main>
        <div className={classes.heroContent}>
          <Container maxWidth="lg">
            {/* End hero unit */}
            <Typography component="h1" variant="h2" align="center" color="textPrimary" gutterBottom>
              海外移住ちゃんねる
            </Typography>
            <Typography variant="h5" align="center" color="textSecondary" paragraph style={{marginBottom: 100}}>
              外国ってどうなんだろうと思う人たちが<br/>好きなことを好きなだけ話し合える場所です。      
            </Typography>
            <Grid container spacing={4}>
              {cards.map((card, index: number) => (
                  <Grid item key={index} xs={12} sm={6} md={4}>
                    <Link to={card.url} style={{textDecoration: 'none'}}>
                      <Card className={classes.card} style={{backgroundColor: '#3f51b5', color: 'white',borderRadius: 0}}>
                        <CardContent className={classes.cardContent}>
                          <Typography variant="h5"　style={{textAlign: 'center'}}>
                            {card.icon}{card.title}
                          </Typography>
                        </CardContent>
                      </Card>
                    </Link>
                  </Grid>
              ))}
            </Grid>
          </Container>
        </div>
      </Main>
    </React.Fragment>
  );
}

export default Home;
